curl 'https://api.coronavirus.data.gov.uk/v1/data?filters=areaType=nation&structure=%7B%22areaType%22:%22areaType%22,%22areaName%22:%22areaName%22,%22areaCode%22:%22areaCode%22,%22date%22:%22date%22,%22covidOccupiedMVBeds%22:%22covidOccupiedMVBeds%22%7D&format=csv' -H 'User-Agent: Mozilla/5.0 (X11; Ubuntu; Linux x86_64; rv:81.0) Gecko/20100101 Firefox/81.0' -H 'Accept: text/html,application/xhtml+xml,application/xml;q=0.9,image/webp,*/*;q=0.8' -H 'Accept-Language: en-GB,en;q=0.5' --compressed -H 'Connection: keep-alive' -H 'Referer: https://coronavirus.data.gov.uk/healthcare' -H 'Cookie: _ga=GA1.3.1147216853.1600719838; _gid=GA1.3.561929123.1601366881; _gat_gtag_UA_161400643_2=1' -H 'Upgrade-Insecure-Requests: 1' -o data/ventilators.csv
echo
echo Downloading deaths by test date data to /data/deaths_testdate.csv
curl 'https://api.coronavirus.data.gov.uk/v1/data?filters=areaType=nation&structure=%7B%22areaType%22:%22areaType%22,%22areaName%22:%22areaName%22,%22areaCode%22:%22areaCode%22,%22date%22:%22date%22,%22newDeaths28DaysByDeathDate%22:%22newDeaths28DaysByDeathDate%22,%22cumDeaths28DaysByDeathDate%22:%22cumDeaths28DaysByDeathDate%22%7D&format=csv' -H 'User-Agent: Mozilla/5.0 (X11; Ubuntu; Linux x86_64; rv:81.0) Gecko/20100101 Firefox/81.0' -H 'Accept: text/html,application/xhtml+xml,application/xml;q=0.9,image/webp,*/*;q=0.8' -H 'Accept-Language: en-GB,en;q=0.5' --compressed -H 'Connection: keep-alive' -H 'Referer: https://coronavirus.data.gov.uk/deaths' -H 'Cookie: _ga=GA1.3.1147216853.1600719838; _gid=GA1.3.561929123.1601366881; _gat_gtag_UA_161400643_2=1' -H 'Upgrade-Insecure-Requests: 1' -o data/deaths_testdate.csv
echo
echo Downloading deaths by reported date data to /data/deaths_reported.csv
curl 'https://api.coronavirus.data.gov.uk/v1/data?filters=areaType=nation&structure=%7B%22areaType%22:%22areaType%22,%22areaName%22:%22areaName%22,%22areaCode%22:%22areaCode%22,%22date%22:%22date%22,%22newDeaths28DaysByPublishDate%22:%22newDeaths28DaysByPublishDate%22,%22cumDeaths28DaysByPublishDate%22:%22cumDeaths28DaysByPublishDate%22%7D&format=csv' -H 'User-Agent: Mozilla/5.0 (X11; Ubuntu; Linux x86_64; rv:81.0) Gecko/20100101 Firefox/81.0' -H 'Accept: text/html,application/xhtml+xml,application/xml;q=0.9,image/webp,*/*;q=0.8' -H 'Accept-Language: en-GB,en;q=0.5' --compressed -H 'Connection: keep-alive' -H 'Referer: https://coronavirus.data.gov.uk/deaths' -H 'Cookie: _ga=GA1.3.1147216853.1600719838; _gid=GA1.3.561929123.1601366881; _gat_gtag_UA_161400643_2=1' -H 'Upgrade-Insecure-Requests: 1' -o data/deaths_reported.csv
